params.data:
[
    {
        "Breach Occurrence Date" : "<p>{{vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].breachOccurrenceDate}}</p>",
        "Breach Detection Date" : "<p>{{vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].breachDetectionDate}}</p>",
        "Brief Description of the Breach" : "<p style='max-width: 500px;'>{{vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].briefDescriptionOfTheBreach}}</p>",
        "Was the PHI/ePHI Encrypted?": "<p>{{(vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].wasThePHIEPHIEncrypted | fromIRI).itemValue}}</p>",
        "Is PHI Misuse or Disclosure Foreseeable?": "<p>{{(vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].isPHIMisuseOrDisclosureForeseeable | fromIRI).itemValue}}</p>",
        "Was the PHI/ePHI Accessed Impermissibly?": "<p>{{(vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].wasThePHIEPHIAccessedImpermissibly | fromIRI).itemValue}}</p>",
        {% if vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].whoAccessedPHIEPHIWithoutPermission %}"Who accessed PHI/ePHI without permission?(If Known)" : "<p style='max-width: 500px;'>{{vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].whoAccessedPHIEPHIWithoutPermission}}</p>",{% endif %}
        "Type Of Data Breach": "<p>{{(vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].typeOfDataBreach | fromIRI).itemValue}}</p>",
        "Location of Breach": "<p><ul style='padding-left: 10px; margin: 0px;'>{% for item in vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].locationOfBreach %}<li>{{(item | fromIRI).itemValue}}</li>{% endfor %}</ul><p>",
        {% if vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].describeOtherLocationOfBreach %}"Describe Other Location of Breach" : "<p style='max-width: 500px;'>{{vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].describeOtherLocationOfBreach}}</p>",{% endif %}
        "Type of PHI Exposed in Breach": "<p><ul style='padding-left: 10px; margin: 0px; list-style-position: outside;'>{% for item in vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].typeOfPHIInvolvedInBreach %}<li>{{(item | fromIRI).itemValue}}</li>{% endfor %}</ul><p>",
        {% if vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].clinicalPHIExposedInBreach %}"Clinical PHI Exposed in Breach" : "<p><ul style='padding-left: 10px; margin: 0px; list-style-position: outside;'>{% for item in vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].clinicalPHIExposedInBreach %}<li>{{(item | fromIRI).itemValue}}</li>{% endfor %}</ul><p>",{% endif %}
        {% if vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].demographicPHIExposedInBreach %}"Demographic PHI Exposed in Breach" : "<p><ul style='padding-left: 10px; margin: 0px; list-style-position: outside;'>{% for item in vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].demographicPHIExposedInBreach %}<li>{{(item | fromIRI).itemValue}}</li>{% endfor %}</ul><p>",{% endif %}
        {% if vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].financialPHIExposedInBreach %}"Financial PHI Exposed in Breach" : "<p><ul style='padding-left: 10px; margin: 0px; list-style-position: outside;'>{% for item in vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].financialPHIExposedInBreach %}<li>{{(item | fromIRI).itemValue}}</li>{% endfor %}</ul><p>",{% endif %}
        {% if vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].typeOfPHIInvolvedInBreachOther %}"Type of PHI Exposed in Breach (Other)" : "<p style='max-width: 500px;'>{{vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].typeOfPHIInvolvedInBreachOther}}</p>",{% endif %}
        "No. of Affected Users" : "<p style='max-width: 500px;'>{{vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].noOfAffectedUsers}}</p>",
        "Safeguards in Place Before Breach": "<p><ul style='padding-left: 10px; margin: 0px; list-style-position: outside;'>{% for item in vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].safeguardsInPlaceBeforeBreach %}<li>{{(item | fromIRI).itemValue}}</li>{% endfor %}</ul><p>",
        "Actions Taken in Response to Breach": "<p><ul style='padding-left: 10px; margin: 0px; list-style-position: outside;'>{% for item in vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].actionsTakenInResponseToBreach %}<li>{{(item | fromIRI).itemValue}}</li>{% endfor %}</ul><p>"{% if vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].describeOtherActionsTaken %},"Describe Other Actions Taken" : "<p style='max-width: 500px;'>{{vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].describeOtherActionsTaken}}</p>"{% endif %}
    }
]

step_variables.placeholder:
{% for item in vars.steps.Get_Compliance_Record_ID[0].dataCompliances[0].description.split('\n') %}
{% if 'Notify HIPAA Security Officer' in item %}
{% set _dummy = item | replace('10060', '9989') %}
{{vars.description.append(_dummy)}}
{% else %}
{{vars.description.append(item)}}
{% endif %}
{% endfor %}

resource.description:
{{vars.demoMode}}

##### The 'Risk Assessment Information' has been reviewed by HSO

{% if vars.steps.Send_Risk_Assessment_Details_to_HSO.input.isReportDpoApproved == 'Rejected' %}##### Go back to <a href="/modules/view-panel/data_compliances/{{vars.complianceUUID}}" target="_parent" rel="noopener"> Data Compliance Record #{{vars.complianceID}}</a> to execute next task <span style="border-radius: 3px; background: #5d636a; padding-top: 5px; padding-bottom: 5px; padding-left: 10px; padding-right: 10px;">**Get Updated Report from HSO**{% elif vars.steps.Send_Risk_Assessment_Details_to_HSO.input.isATechnicalFixRequired == 'Yes' %}##### Go back to <a href="/modules/view-panel/data_compliances/{{vars.complianceUUID}}" target="_parent" rel="noopener"> Data Compliance Record #{{vars.complianceID}}</a> to execute next task <span style="border-radius: 3px; background: #5d636a; padding-top: 5px; padding-bottom: 5px; padding-left: 10px; padding-right: 10px;">**Perform Actions to Apply Technical Fix**</span>{% else %}##### Go back to <a href="/modules/view-panel/data_compliances/{{vars.complianceUUID}}" target="_parent" rel="noopener"> Data Compliance Record #{{vars.complianceID}}</a> to execute next task <span style="border-radius: 3px; background: #5d636a; padding-top: 5px; padding-bottom: 5px; padding-left: 10px; padding-right: 10px;">**Report Crime to Law Enforcement Agency**</span>{% endif %}

